sourcesContent:
import models from "../models"

export default {
    register: async(req,res) => {
        try {
            let data = req.body;

            let exits_cupone = await models.Cupone.findOne({code: data.code});

            if(exits_cupone){
                res.status(200).json({
                    message:403,
                    message_text: "EL CODIGO DEL CUPON YA EXISTE"
                });
                return;
            }

            let cupone = await models.Cupone.create(data);

            res.status(200).json({
                message: 200,
                message_text: "El cupón se registró satisfactoriamente",
                cupone:cupone
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
    update: async(req,res) => {
        try {
            let data = req.body;

            let exits_cupone = await models.Cupone.findOne({code: data.code,_id: {$ne: data._id}});

            if(exits_cupone){
                res.status(200).json({
                    message:403,
                    message_text: "EL CODIGO DEL CUPON YA EXISTE"
                });
                return;
            }

            let cupone = await models.Cupone.findByIdAndUpdate({_id: data._id},data);

            let cuponeT = await models.Cupone.findById({_id: data._id});

            res.status(200).json({
                message: 200,
                message_text: "El cupón se actualizó satisfactoriamente",
                cupone:cuponeT,
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
    delete: async(req,res) => {
        try {
            let _id = req.query._id;

            await models.Cupone.findByIdAndDelete({_id: _id});

            res.status(200).json({
                message: 200,
                message_text: "El cupón se eliminó satisfactoriamente",
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
    list: async(req,res) => {
        try {
            let search = req.query.search;

            let cupones = await models.Cupone.find({
                $or:[
                    {"code": new RegExp(search, "i")},
                ]
            }).sort({'createdAt': -1});

            res.status(200).json({
                message: 200,
                cupones: cupones,
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
    show: async(req,res) => {
        try {
            let cupone_id = req.query.cupone_id;

            let cupon = await models.Cupone.findOne({_id: cupone_id});

            res.status(200).json({
                message: 200,
                cupon: cupon,
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
    config: async(req,res) => {
        try {
            let Products = await models.Product.find({ state: 2, condition: { $ne: 3 } });
            let Categories = await models.Categorie.find({state:1});

            res.status(200).json({
                message: 200,
                products: Products,
                categories: Categories,
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
}

