                    {"code": new RegExp(search, "i")},
                ]
            }).sort({'createdAt': -1});

            res.status(200).json({
                message: 200,
                cupones: cupones,
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
    show: async(req,res) => {
        try {
            let cupone_id = req.query.cupone_id;

            let cupon = await models.Cupone.findOne({_id: cupone_id});

            res.status(200).json({
                message: 200,
                cupon: cupon,
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
    config: async(req,res) => {
        try {
            let Products = await models.Product.find({ state: 2, condition: { $ne: 3 } });
            let Categories = await models.Categorie.find({state:1});

            res.status(200).json({
                message: 200,
                products: Products,
                categories: Categories,
            });

        } catch (error) {
            res.status(500).send({
                message: "OCURRIO UN ERROR",
            });
        }
    },
}

